sys_script_include.script:
function validateEmailAddress(emailStr) {
    // Use javascript coercion to guarantee emailStr is a string
    emailStr = emailStr + '';

    /* regular expression
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    */

	var regex = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    // Compare emailStr against the allowed syntax specified in the regulare expression
    // If emailStr has allowed syntax, return true, otherwise return false
    if (emailStr.match(regex)) {
        return true;
    } else {
        return false;
    }
}
